import React, { useState, useMemo } from 'react';

import camera from '../../assets/camera.svg'

import api from '../../services/api'

import './styles.css';

export default function New(){
    const [thumbnail, setThumbnail] = useState(null);
    const [company, setCompany] = useState('');
    const [techs, setTechs] = useState('');
    const [price, setPrice] = useState('');

    const preview = useMemo(() => {
        return thumbnail ? URL.createObjectURL(thumbnail) : null;
    }, [thumbnail])

    async function handleSubmit() {
        const data = new FormData();
        const user_id = localStorage.getItem('user')

        data.append('thumbnail', thumbnail)
        data.append('company', company)
        data.append('techs', techs)
        data.append('price', price)

        await api.post('/spots', data, {
            headers: { user_id}
        })

    };


    return(
        <form onSubmit={handleSubmit}>

            <label id="thumbnail" style={{ backgroundImage: `url(${preview})`}} className={thumbnail ? 'has-thumbnail' : ''}>
                <input type="file" onChange={event => setThumbnail(event.target.files[0])}/>
                <img src={camera} alt="Select img"/>
            </label>

            <label htmlFor="company">EMPRESA *</label>
            <input 
                id="company"
                placeholder="Sua empresa incricel"
                value={company}
                onChange={event => setCompany(event.targer.value)}
            />

            <label htmlFor="techs">TECNOLOGIAS *<span>(separadas por virgula)</span></label>
            <input 
                id="techs"
                placeholder="Quais tecnologias usam?"
                value={techs}
                onChange={event => setTechs(event.targer.value)}
            />

            <label htmlFor="price">VALOR DA DIARIA *<span>(em branco para GRATUITO)</span></label>
            <input 
                id="price"
                placeholder="Valor cobrado por dia"
                value={price}
                onChange={event => setPrice(event.targer.value)}
            />

            <button type='submit' className='btn'>Cadastrar</button>
        </form> 
    )
}
